import React, { useState, useEffect } from "react";
import {
  ChatContainer,
  Conversation,
  MessageList,
  MessageInput,
  ConversationHeader,
  Message,
  Loader,
} from "@chatscope/chat-ui-kit-react";
import ApiNotFound from "./ApiNotFound.js";
import TypingInd from "./TypingIndicator.js";
import "@chatscope/chat-ui-kit-styles/dist/default/styles.min.css";
import "./ChatComponent.css";

const ChatComponent = () => {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [alive, setAlive] = useState(true);

  useEffect(() => {
    const checkStatus = async () => {
      try {
        fetch("http://localhost:5001/health", {
          method: "GET",
          headers: {
            "Content-Type": "Application/json",
          },
        })
          .then((response) => response.json())
          .then((data) => {
            console.log(data);
            if (data.status === "up") {
              setAlive(true);
            } else {
              setAlive(false);
            }
          })
          .catch((error) => {
            console.error("API check failed", error);
            setAlive(false);
          });
      } catch (error) {
        console.error("API check failed", error);
        setAlive(false);
      }
    };

    // Call the checkStatus function
    checkStatus();

    const interval = setInterval(checkStatus, 10000); // Check every 10 seconds
    return () => clearInterval(interval); // Cleanup interval on unmount
  }, []); // The empty array causes this effect to only run on mount and unmount

  const sendMessage = (content) => {
    setIsLoading(true);

    const userMessage = {
      message: content,
      direction: "outgoing",
      sender: "Me", // Add sender property
    };

    setMessages((prevMessages) => [...prevMessages, userMessage]);

    // fetch("http://localhost:5001/recommend", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //   },
    //   body: JSON.stringify({ message: content }),
    // })
    //   .then((response) => {
    //     if (!response.ok) {
    //       throw new Error(`HTTP error! status: ${response.status}`);
    //     }
    //     return response.json();
    //   })
    //   .then((responseData) => {
    //     const botMessage = {
    //       message: responseData.response,
    //       direction: "incoming",
    //       sender: "Chatbot", // Add sender property
    //     };
    //     // Add the bot's response message
    //     setMessages((prevMessages) => [...prevMessages, botMessage]);
    //   })
    //   .catch((error) => {
    //     console.error("Could not send the message to the API", error);
    //   })
    //   .finally(() => {
    //     setIsLoading(false); // End loading
    //   });
  };

  return (
    <div className="chat-container">
      {alive ? (
        <ChatContainer>
          <Conversation>
            <MessageList typingIndicator={isLoading && <TypingInd />}>
              {messages.map((msg, index) => (
                <Message
                  key={index}
                  model={{
                    message: msg.message,
                    direction: msg.direction,
                    sender: msg.sender,
                  }}
                />
              ))}
            </MessageList>
            <MessageInput
              placeholder="Type message here"
              onSend={sendMessage}
              attachButton={false}
            />
          </Conversation>
        </ChatContainer>
      ) : (
        <ApiNotFound />
      )}
    </div>
  );
};

export default ChatComponent;
