import {
  ChatContainer,
  MessageList,
  MessageInput,
  ConversationHeader,
  Message,
} from "@chatscope/chat-ui-kit-react";
import "@chatscope/chat-ui-kit-styles/dist/default/styles.min.css"; // Ensure you import the styles

const ChatComponent = () => {
  const [messages, setMessages] = useState([]);

  const sendMessage = async (content) => {
    // Same sendMessage function as before
    // ...
  };

  return (
    <div style={{ position: "relative", height: "100vh" }}>
      {" "}
      {/* Adjusted for full screen height */}
      <ChatContainer>
        <ConversationHeader>
          {/* Place your conversation header content here */}
        </ConversationHeader>
        <MessageList>
          {messages.map((msg, index) => (
            <Message
              key={index}
              model={{
                message: msg.message,
                direction: msg.direction,
              }}
            />
          ))}
        </MessageList>
        {/* Adjust the styling below as needed */}
        <div style={{ position: "absolute", bottom: "0", width: "100%" }}>
          <MessageInput
            placeholder="Type message here"
            onSend={sendMessage}
            style={{ height: "3rem", fontSize: "1.25rem" }} // Adjust height and font size here
          />
        </div>
      </ChatContainer>
    </div>
  );
};

export default ChatComponent;
