import React, { useState } from "react";
import {
  ChatContainer,
  MessageList,
  MessageInput,
  ConversationHeader,
  Message, // Correctly imported Message component
} from "@chatscope/chat-ui-kit-react";

const ChatComponent = () => {
  const [messages, setMessages] = useState([]);

  const sendMessage = async (content) => {
    // Add the user's message with direction 'outgoing'
    const userMessage = {
      message: content,
      direction: "outgoing",
    };

    setMessages((prevMessages) => [...prevMessages, userMessage]);

    try {
      const response = await fetch("http://127.0.0.1:5000/recommend", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ message: content }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const responseData = await response.json();
      const botMessage = {
        message: responseData.response,
        direction: "incoming", // Responses from the bot are 'incoming'
      };

      // Add the bot's response message
      setMessages((prevMessages) => [...prevMessages, botMessage]);
    } catch (error) {
      console.error("Could not send the message to the API", error);
    }
  };

  return (
    <div style={{ position: "relative", height: "500px" }}>
      <ChatContainer>
        <ConversationHeader>
          {/* Place your conversation header content here */}
        </ConversationHeader>
        <MessageList>
          {messages.map((msg, index) => (
            <Message
              key={index}
              model={{
                message: msg.message,
                direction: msg.direction,
              }}
            />
          ))}
        </MessageList>
        <MessageInput placeholder="Type message here" onSend={sendMessage} />
      </ChatContainer>
    </div>
  );
};

export default ChatComponent;
