import React, { useState, useEffect } from "react";
import {
  ChatContainer,
  MessageList,
  MessageInput,
  ConversationHeader,
  Message,
  Loader,
} from "@chatscope/chat-ui-kit-react";
import ApiNotFound from "./ApiNotFound.js";
import TypingIndicator from "./TypingIndicator.js";
import "@chatscope/chat-ui-kit-styles/dist/default/styles.min.css"; // Ensure you import the styles
import "./ChatComponent.css"; // Import your custom CSS for this component

const ChatComponent = () => {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [alive, setAlive] = useState(true);

  //   useEffect(() => {
  //     const checkStatus = async () => {
  //       try {
  //         fetch("http://localhost:5001/health", {
  //           method: "GET",
  //           headers: {
  //             "Content-Type": "Application/json",
  //           },
  //         })
  //           .then((response) => response.json())
  //           .then((data) => {
  //             console.log(data);
  //             if (data.status === "up") {
  //               setAlive(true);
  //             } else {
  //               setAlive(false);
  //             }
  //           })
  //           .catch((error) => {
  //             console.error("API check failed", error);
  //             setAlive(false);
  //           });
  //       } catch (error) {
  //         console.error("API check failed", error);
  //         setAlive(false);
  //       }
  //     };

  //     // Call the checkStatus function
  //     checkStatus();

  //     const interval = setInterval(checkStatus, 10000); // Check every 10 seconds
  //     return () => clearInterval(interval); // Cleanup interval on unmount
  //   }, []); // The empty array causes this effect to only run on mount and unmount

  const sendMessage = (content) => {
    setIsLoading(true);

    const userMessage = {
      message: content,
      direction: "outgoing",
    };

    setMessages((prevMessages) => [...prevMessages, userMessage]);

    fetch("http://localhost:5001/recommend", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ message: content }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((responseData) => {
        const botMessage = {
          message: responseData.response,
          direction: "incoming", // Responses from the bot are 'incoming'
        };
        // Add the bot's response message
        setMessages((prevMessages) => [...prevMessages, botMessage]);
      })
      .catch((error) => {
        console.error("Could not send the message to the API", error);
      })
      .finally(() => {
        setIsLoading(false); // End loading
      });
  };

  return (
    <div className="chat-container">
      {alive ? (
        <ChatContainer>
          <ConversationHeader>
            {/* Place your conversation header content here */}
          </ConversationHeader>
          <MessageList typingIndicator={isLoading && <TypingIndicator />}>
            {" "}
            {/* Use typingIndicator prop for loader */}
            {messages.map((msg, index) => (
              <Message
                key={index}
                model={{
                  message: msg.message,
                  direction: msg.direction,
                }}
              />
            ))}
          </MessageList>
          <MessageInput
            placeholder="Type message here"
            onSend={sendMessage}
            attachButton={false} // Set to false if you don't want the attach button
          />
        </ChatContainer>
      ) : (
        <>
          <ApiNotFound />
        </>
      )}
    </div>
  );
};

export default ChatComponent;
