import React, { useState } from "react";
import {
  ChatContainer,
  MessageList,
  MessageInput,
  ConversationHeader,
  Message,
  Loader,
} from "@chatscope/chat-ui-kit-react";
import "@chatscope/chat-ui-kit-styles/dist/default/styles.min.css"; // Ensure you import the styles
import "../App.css"; // Import your custom CSS for dark mode

const ChatComponent = () => {
  // ... your existing useState hooks and sendMessage function

  return (
    <div
      className="chat-container"
      style={{ position: "relative", height: "100vh" }}
    >
      <ChatContainer>
        <ConversationHeader>
          {/* Place your conversation header content here */}
        </ConversationHeader>
        <MessageList typingIndicator={isLoading && <Loader />}>
          {" "}
          {/* Use typingIndicator prop for loader */}
          {messages.map((msg, index) => (
            <Message
              key={index}
              model={{
                message: msg.message,
                direction: msg.direction,
              }}
            />
          ))}
        </MessageList>
        <MessageInput
          placeholder="Type message here"
          onSend={sendMessage}
          attachButton={false} // Set to false if you don't want the attach button
        />
      </ChatContainer>
    </div>
  );
};

export default ChatComponent;
