import React, { useState, useEffect } from "react";
import {
  MessageBox,
  ChatItem,
  Input,
  Button,
  SystemMessage,
} from "react-chat-elements";
import "react-chat-elements/dist/main.css";
import ApiNotFound from "./ApiNotFound.js";
import TypingInd from "./TypingIndicator.js";
import "./ChatComponent.css";

const ChatComponent = () => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [alive, setAlive] = useState(true);

  useEffect(() => {
    const checkStatus = async () => {
      try {
        fetch("http://localhost:5001/health", {
          method: "GET",
          headers: {
            "Content-Type": "Application/json",
          },
        })
          .then((response) => response.json())
          .then((data) => {
            console.log(data);
            if (data.status === "up") {
              setAlive(true);
            } else {
              setAlive(false);
            }
          })
          .catch((error) => {
            console.error("API check failed", error);
            setAlive(false);
          });
      } catch (error) {
        console.error("API check failed", error);
        setAlive(false);
      }
    };

    // Call the checkStatus function
    checkStatus();

    const interval = setInterval(checkStatus, 10000); // Check every 10 seconds
    return () => clearInterval(interval); // Cleanup interval on unmount
  }, []); // The empty array causes this effect to only run on mount and unmount

  const sendMessage = (content) => {
    setIsLoading(true);

    const newMessage = {
      position: "right",
      type: "text",
      text: inputValue,
      date: new Date(),
    };
    setMessages([...messages, newMessage]);
    setInputValue("");

    // fetch("http://localhost:5001/recommend", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //   },
    //   body: JSON.stringify({ message: content }),
    // })
    //   .then((response) => {
    //     if (!response.ok) {
    //       throw new Error(`HTTP error! status: ${response.status}`);
    //     }
    //     return response.json();
    //   })
    //   .then((responseData) => {
    //     const botMessage = {
    //       message: responseData.response,
    //       direction: "incoming",
    //       sender: "Chatbot", // Add sender property
    //     };
    //     // Add the bot's response message
    //     setMessages((prevMessages) => [...prevMessages, botMessage]);
    //   })
    //   .catch((error) => {
    //     console.error("Could not send the message to the API", error);
    //   })
    //   .finally(() => {
    //     setIsLoading(false); // End loading
    //   });
  };

  return (
    <div>
      {messages.map((msg, index) => (
        <MessageBox
          key={index}
          position={msg.position}
          type={msg.type}
          text={msg.text}
          dateString={msg.date.toLocaleTimeString()}
        />
      ))}
      <Input
        placeholder="Type here..."
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        rightButtons={
          <Button
            color="white"
            backgroundColor="black"
            text="Send"
            onClick={sendMessage}
          />
        }
      />
    </div>
  );
};

export default ChatComponent;
