import React, { useState, useEffect } from "react";
import {
  ChatContainer,
  MessageList,
  MessageInput,
  ConversationHeader,
  Message,
  Loader,
} from "@chatscope/chat-ui-kit-react";
import ApiNotFound from "./ApiNotFound.js";
import "@chatscope/chat-ui-kit-styles/dist/default/styles.min.css"; // Ensure you import the styles
import "./ChatComponent.css"; // Import your custom CSS for this component

const ChatComponent = () => {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [alive, setAlive] = useState(false);

  useEffect(() => {
    const checkStatus = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/status");
        if (response.ok) {
          const data = await response.json();
          // Check the status returned by the API
          if (data.status === "up") {
            setAlive(true);
          } else {
            setAlive(false);
          }
        } else {
          // If the API call was unsuccessful, set alive to false
          setAlive(false);
        }
      } catch (error) {
        console.error("API check failed", error);
        setAlive(false);
      }
    };

    // Call the checkStatus function
    checkStatus();

    const interval = setInterval(checkStatus, 10000); // Check every 10 seconds
    return () => clearInterval(interval); // Cleanup interval on unmount
  }, []); // The empty array causes this effect to only run on mount and unmount

  const sendMessage = async (content) => {
    setIsLoading(true);

    const userMessage = {
      message: content,
      direction: "outgoing",
    };

    setMessages((prevMessages) => [...prevMessages, userMessage]);

    try {
      const response = await fetch("http://127.0.0.1:5000/recommend", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ message: content }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const responseData = await response.json();
      const botMessage = {
        message: responseData.response,
        direction: "incoming", // Responses from the bot are 'incoming'
      };

      // Add the bot's response message
      setMessages((prevMessages) => [...prevMessages, botMessage]);
    } catch (error) {
      console.error("Could not send the message to the API", error);
    } finally {
      setIsLoading(false); // End loading
    }
  };

  return (
    <div className="chat-container">
      {alive ? (
        <ChatContainer>
          <ConversationHeader>
            {/* Place your conversation header content here */}
          </ConversationHeader>
          <MessageList typingIndicator={isLoading && <Loader />}>
            {" "}
            {/* Use typingIndicator prop for loader */}
            {messages.map((msg, index) => (
              <Message
                key={index}
                model={{
                  message: msg.message,
                  direction: msg.direction,
                }}
              />
            ))}
          </MessageList>
          <MessageInput
            placeholder="Type message here"
            onSend={sendMessage}
            attachButton={false} // Set to false if you don't want the attach button
          />
        </ChatContainer>
      ) : (
        <>
          <ApiNotFound />
        </>
      )}
    </div>
  );
};

export default ChatComponent;
