import React, { useState } from "react";
import {
  ChatContainer,
  MessageList,
  MessageInput,
  ConversationHeader,
  Message,
  Loader,
} from "@chatscope/chat-ui-kit-react";
import "@chatscope/chat-ui-kit-styles/dist/default/styles.min.css"; // Ensure you import the styles
import "../App.css"; // Import your custom CSS for dark mode

const ChatComponent = () => {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const sendMessage = async (content) => {
    setIsLoading(true); // Start loading
    // Add the user's message with direction 'outgoing'
    const userMessage = {
      message: content,
      direction: "outgoing",
    };

    setMessages((prevMessages) => [...prevMessages, userMessage]);

    try {
      const response = await fetch("http://127.0.0.1:5000/recommend", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ message: content }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const responseData = await response.json();
      const botMessage = {
        message: responseData.response,
        direction: "incoming", // Responses from the bot are 'incoming'
      };

      // Add the bot's response message
      setMessages((prevMessages) => [...prevMessages, botMessage]);
    } catch (error) {
      console.error("Could not send the message to the API", error);
    } finally {
      setIsLoading(false); // End loading
    }
  };

  return (
    <div
      className="chat-container"
      style={{ position: "relative", height: "100vh" }}
    >
      <ChatContainer>
        <ConversationHeader>
          {/* Place your conversation header content here */}
        </ConversationHeader>
        <MessageList typingIndicator={isLoading && <Loader />}>
          {" "}
          {/* Use typingIndicator prop for loader */}
          {messages.map((msg, index) => (
            <Message
              key={index}
              model={{
                message: msg.message,
                direction: msg.direction,
              }}
            />
          ))}
        </MessageList>
        <MessageInput
          placeholder="Type message here"
          onSend={sendMessage}
          attachButton={false} // Set to false if you don't want the attach button
        />
      </ChatContainer>
    </div>
  );
};

export default ChatComponent;
